import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import{AiOutlineEye,AiOutlineEyeInvisible} from "react-icons/ai"
import toast from "react-hot-toast";

const LoginForm=({setIsLoggedIn})=>{
  
const [formData,setFormData]=useState({
    email:"",password:""
})

const [userPassword,setUserPassowrd]=useState(false)
const navigate=useNavigate();

function changeHandler(event){
   setFormData((preData)=>({
    ...preData,
    [event.target.name]:event.target.value
   })
   

  )
}

function submitHandler(event){
    event.preventDefault();
    toast.success("Logged in");
    navigate("/dashboard");
    
setIsLoggedIn(false);
   

}



    return(
<form onSubmit={submitHandler}>
    <label>
        <p  className="text-medium font-semibold">
            Email Address<sup>*</sup>
        </p>
       
               
        <input className="rounded bg-richblack-700 "
        required
        type="email"
        value={formData.email}
        name="email"
        placeholder="Enter Email"
        onChange={changeHandler}
        />
    </label>
    <label>
        <p  className="text-medium font-semibold">
            Password<sup>*</sup>
        </p>
        <input 
        className="rounded bg-richblack-700  absolute"
        required
        type={userPassword?('text'):('password')}
        value={formData.password}
        name="password"
        placeholder="Enter password"
        onChange={changeHandler}
       
/>
        <span onClick={()=>setUserPassowrd((prev)=>!prev)}
        className=""
      >
           {userPassword?(<AiOutlineEyeInvisible/>):(<AiOutlineEye/>)} 
        </span>
        
        <Link to="/">
            <p>
                forgot passowrd
            </p>
        </Link>
    </label>

    <button>
        Sign in 
    </button>
  
</form>

    );
}

export default LoginForm;
