import { useEffect, useState} from "react";

function Dashboard() {
const [empData,setEmpData]=useState('')
const getUserData=async()=>{
  try{
    const getStudent=await fetch(
      'http://localhost:4000/api/v1/getdata/',
      {
        method: "GET",
        headers:{         
            'Content-Type':'application/json'
        
        },
      }
    );
    const {data}=await getStudent.json();
    setEmpData(data);
  console.log("response is ",data)

  }
  catch(error){
    console.log("error is ",error)
  }
}
useEffect(()=>{
  getUserData();
},[])
console.log(empData);

  return (
    <div className="flex flex-1 justify-center items-center text-black text-3xl ">
      {/* <h1>{empData.image}</h1> */}
      {/* <img src={empData.image}></img> */}
      <div className="flex w-screen h-screen mt-10">
      <div className="w-[20%]  border-red-300 flex items-center  flex-col">
        <h1 className="p-3 text-black font-semibold text-3xl">Your Profile</h1>
        <p className="p-3 text-black font-semibold text-3xl"><span >{empData.firstName}</span><span>{empData.lastName}</span></p>
        <img src={empData.image} alt="yourImage" srcset="" className="w-20 h-20 rounded-[50%]" />
        <p className="p-3 text-shark-300 font-semibold text-sm">{empData.email}</p>
        <p className="p-3 text-black font-semibold text-3xl">{empData.course}</p>
      </div>
      <div className="w-[80%]">

      <div className="tex bg-white-50 shadow-slate-200 shadow-xl rounded-xl ">
    
    <div className=" h-[400px] w-[400px] flex flex-col justify-center items-center">
        <div>
        <img  alt="imagenotupload" height={255} width={255}/>
        </div>
        <div>
        <h5 className="text-3xl font-bold shadow-inner "></h5>
        </div>
        <div className="text-xl font-bold text-green-500">
        <p></p>
        </div>
  
    </div>
    <div className="flex justify-evenly mb-4">
    <button className="text-xl text-white p-2 font-bold bg-blue-700 rounded-lg mb-">KnowMore</button>
    <button className="text-xl text-white  bg-blue-700 p-2 font-bold rounded-lg" 
    >Learn </button>
    </div>
  </div>

      </div>
      </div>
   

    </div>
  );
}

export default Dashboard;
