import React from "react";
import { Link } from "react-router-dom";
// import logo from "../assets/Logo.svg"
import onlinelogo from '../assets/onlinelogo.png'
import toast from "react-hot-toast";
const Navbar=(props)=>{
    let isLoggedIn=props.isLoggedIn;
    let setIsLoggedIn=props.setIsLoggedIn;

    return(
        <div className="flex justify-evenly items-center w-full  max-w[1160] bg-white shadow-md">
           <Link to="/"> 
           <img src={onlinelogo} alt="Logo" width={50} height={15} loading="lazy"/>
           </Link>
           <nav>
            <ul className="flex   gap-x-6 text-white ">
                <li>
                    <Link to="/">Home</Link>
                </li>
                <li>
                    <Link to="/">About</Link>
                </li>
                <li>
                    <Link to="/">Contect</Link>
                </li>
            </ul>
           </nav>

            {/* Login,signup, Logout,Dashboard  */}
            <dvi className="flex  items-center gap-4 ">
                {isLoggedIn &&
                    <Link to="/login">
                        <button className="bg-blue-500  text-white py-[8px] px-[12px] rounded-[8px] border-blue-700 font-semibold">
                            Log in
                        </button>
                    </Link>
                }
                {isLoggedIn &&
                    <Link to="/signup">
                        <button className="bg-blue-800  text-white  py-[8px] px-[12px] rounded-[8px] border-blue-700">
                            Sign Up
                        </button>
                    </Link>
                }
                {!isLoggedIn &&
                    <Link to="/">
                        <button onClick={()=>{
                            setIsLoggedIn(true)
                            toast.success("Logged Out") 
                          
                        }}
                        className="bg-richblack-800 text-richblack-100 py-[8px] px-[12px] rounded-[8px] border-richblack-700"
                        >
                            LogOut
                        </button>
                    </Link>
                }
                { !isLoggedIn &&
                    <Link to="/dashboard">
                        <button className="bg-richblack-800 text-richblack-100  py-[8px] px-[12px] rounded-[8px] border-richblack-700">
                           Dashboard
                        </button>
                    </Link>
                }
            </dvi>

        </div>

    );
}
export default Navbar;
