import React from "react"
// import frameImage from "../assets/frame.png"
import SignUpForm from "./SignUPForm";
import LoginForm from "./LoginFrom";
import {FcGoogle} from "react-icons/fc"


const Template=({title,desc1,desc2,image,formType,setIsLoggedIn })=>{
return(
    <div className=" mt-3 flex flex-col w-full max-w-[500px] py-8 mx-auto gap-x-12 gap-y-0 text-slate-700 justify-center   bg-slate-300 rounded-md">
        <h1 className="text-4xl font-bold">{title}</h1>
        <div className=" max-w-[450px] mx-0 "> 
                  
        {/* <p  className="text-xl font-semibold">
            <span>{desc1}</span>
            <span>{desc2}</span>
        </p> */}
{formType==="signup"?(<SignUpForm setIsLoggedIn={setIsLoggedIn}/>):(<LoginForm setIsLoggedIn={setIsLoggedIn}/>)
}
<div className="flex items-center">
    <div className="w-full h-[0.5px] bg-slate-300  "></div>
<p  className="text-medium font-semibold text-center">OR</p>
<div className=" bg-slate-300 w-full h-[0.5px] "></div>
</div>

<button  className=" font-semibold flex items-center justify-center rounded border-white bg-richblack-100 
 text-richblack-900 w-full h-10  mt-2 ">
    <FcGoogle />
    Sign Up with Google
    </button>

</div>
 



{/*  
     <div className="relative w-11/12 max-w-[450px] ">
 <img src={frameImage} alt="pattern" width={558} height={504} loading="lazy"

 />   


<img src={image} alt="pattern" width={558} height={490} loading="lazy"
className="absolute bottom-4 -left-4"
 />


</div> */}

     


    </div>
);
}
export default Template;
