import React, { useState } from "react";
import{AiOutlineEye,AiOutlineEyeInvisible} from "react-icons/ai"
const SignUpForm = () => {

    const [formData, setFormData] = useState({
        firstName: "", lastName: "", email: "", password: "", conformPassword: ""
    })

    const [userPassword, setUserPassowrd] = useState(false)


    function changeHandler(event) {
        setFormData((preData) => ({
            ...preData,
            [event.target.name]: event.target.value
        }))
    }


    return (
        <div>
            {/* student insturctor tab   */}
            <div>
                <button>
                    Student
                </button>
                <button>
                    Instructor
                </button>
            </div>

            <form>

                {/* first name last name containre */}
                <div>
                    <label>
                        <p>First Name</p><sup>*</sup>
                        <input
                            required
                            type="text"
                            name="firstName"
                            value={formData.firstName}
                            onChange={changeHandler}
                            placeholder="Enter Firs Name"
                        />
                    </label>
                    <label>
                        <p>Last Name</p><sup>*</sup>
                        <input
                            required
                            type="text"
                            name="lastName"
                            value={formData.lastName}
                            onChange={changeHandler}
                            placeholder="Enter Last Name"
                        />
                    </label>

                </div>


                <label>
                    <p>Email Address</p><sup>*</sup>
                    <input
                        required
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={changeHandler}
                        placeholder="Enter Email Address"
                    />
                </label>

                <div>
                    <label>
                        <p>Creat Password</p><sup>*</sup>
                        <input
                            required
                            type={userPassword ? ("text") : ("password")}
                            name="password"
                            value={formData.password}
                            onChange={changeHandler}
                            placeholder="Enter password"
                        />
                        <span onClick={() => setUserPassowrd((prev) => !prev)}>
                            {userPassword ? (<AiOutlineEyeInvisible />) : (<AiOutlineEye />)}
                        </span>
                    </label>
                    <label>
                        <p>Conform Password</p><sup>*</sup>
                        <input
                            required
                            type={userPassword ? ("text") : ("password")}
                            name="conformPassword"
                            value={formData.conformPassword}
                            onChange={changeHandler}
                            placeholder="Conform password"
                        />
                        <span onClick={() => setUserPassowrd((prev) => !prev)}>
                            {userPassword ? (<AiOutlineEyeInvisible />) : (<AiOutlineEye />)}
                        </span>
                    </label>
                </div>
                

                <button>
        Creat Account 
    </button>

            </form>

        </div>
    );
}
export default SignUpForm;
